                <div class="highlight"><pre><div class='line' id='LC1'><span class="cp">&lt;?PHP</span></div><div class='line' id='LC2'><span class="c1">// Requires AcField. Include AcField directly which will include this.</span></div><div class='line' id='LC3'><span class="c1">//////////////////////////////////////////////////////////////////////////////////////////////////////////////////</span></div><div class='line' id='LC4'><span class="sd">/**</span></div><div class='line' id='LC5'><span class="sd"> *  AcList represents the backend controller for fields which have multiple simultaneous values (i.e. lists),</span></div><div class='line' id='LC6'><span class="sd"> *  including dropdown comboboxes and SELECT boxes.</span></div><div class='line' id='LC7'><span class="sd"> * </span></div><div class='line' id='LC8'><span class="sd"> */</span></div><div class='line' id='LC9'><span class="k">abstract</span> <span class="k">class</span> <span class="nc">AcList</span> <span class="k">extends</span> <span class="nx">AcField</span></div><div class='line' id='LC10'><span class="p">{</span>	</div><div class='line' id='LC11'>	<span class="k">public</span> <span class="nv">$options_field</span><span class="p">,</span> <span class="nv">$options_table</span><span class="p">,</span> <span class="nv">$options_pkey</span><span class="p">,</span> <span class="nv">$options_loadability</span><span class="p">;</span></div><div class='line' id='LC12'><br/></div><div class='line' id='LC13'>	<span class="sd">/**</span></div><div class='line' id='LC14'><span class="sd">	 * Constructor follows much the same format as AcField</span></div><div class='line' id='LC15'><span class="sd">	 *  more comments to come...</span></div><div class='line' id='LC16'><span class="sd">	 */</span></div><div class='line' id='LC17'>	<span class="k">function</span> <span class="nf">AcList</span><span class="p">(</span><span class="nv">$field</span><span class="p">,</span> <span class="nv">$table</span><span class="p">,</span> <span class="nv">$id</span><span class="p">,</span> <span class="nv">$loadable</span><span class="p">,</span> <span class="nv">$savable</span><span class="p">)</span></div><div class='line' id='LC18'>	<span class="p">{</span></div><div class='line' id='LC19'>		<span class="k">parent</span><span class="o">::</span><span class="na">__construct</span><span class="p">(</span><span class="nv">$field</span><span class="p">,</span><span class="nv">$table</span><span class="p">,</span><span class="nv">$id</span><span class="p">,(</span><span class="nx">int</span><span class="p">)</span><span class="nv">$loadable</span><span class="p">,(</span><span class="nx">int</span><span class="p">)</span><span class="nv">$savable</span><span class="p">);</span></div><div class='line' id='LC20'>		<span class="nv">$tmp</span> <span class="o">=</span> <span class="o">&amp;</span><span class="k">parent</span><span class="o">::</span><span class="na">get_session_object</span><span class="p">();</span></div><div class='line' id='LC21'>		<span class="nv">$this</span><span class="o">-&gt;</span><span class="na">options_field</span> <span class="o">=</span> <span class="nv">$tmp</span><span class="p">[</span><span class="s1">&#39;options_field&#39;</span><span class="p">]</span> <span class="o">=</span> <span class="nv">$field</span><span class="p">;</span> <span class="c1">//default to same values.</span></div><div class='line' id='LC22'>		<span class="nv">$this</span><span class="o">-&gt;</span><span class="na">options_table</span> <span class="o">=</span> <span class="nv">$tmp</span><span class="p">[</span><span class="s1">&#39;options_table&#39;</span><span class="p">]</span> <span class="o">=</span> <span class="nv">$table</span><span class="p">;</span></div><div class='line' id='LC23'>		<span class="nv">$this</span><span class="o">-&gt;</span><span class="na">options_pkey</span> <span class="o">=</span> <span class="nv">$tmp</span><span class="p">[</span><span class="s1">&#39;options_pkey&#39;</span><span class="p">]</span> <span class="o">=</span> <span class="nv">$id</span><span class="p">;</span> 				</div><div class='line' id='LC24'>		<span class="nv">$this</span><span class="o">-&gt;</span><span class="na">type</span> <span class="o">=</span> <span class="nv">$tmp</span><span class="p">[</span><span class="s1">&#39;type&#39;</span><span class="p">]</span> <span class="o">=</span> <span class="s2">&quot;multi&quot;</span><span class="p">;</span></div><div class='line' id='LC25'>		<span class="nv">$this</span><span class="o">-&gt;</span><span class="na">options_loadability</span> <span class="o">=</span> <span class="nv">$tmp</span><span class="p">[</span><span class="s1">&#39;options_loadability&#39;</span><span class="p">]</span> <span class="o">=</span> <span class="nv">$loadable</span><span class="p">;</span></div><div class='line' id='LC26'>	<span class="p">}</span></div><div class='line' id='LC27'><br/></div><div class='line' id='LC28'>	<span class="c1">////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////</span></div><div class='line' id='LC29'>	<span class="c1">//</span></div><div class='line' id='LC30'>	<span class="k">function</span> <span class="nf">differentiate_options</span><span class="p">(</span><span class="nv">$field</span><span class="p">,</span> <span class="nv">$table</span><span class="p">,</span> <span class="nv">$id</span><span class="p">,</span> <span class="nv">$populatability</span><span class="p">)</span></div><div class='line' id='LC31'>	<span class="p">{</span></div><div class='line' id='LC32'>		<span class="nv">$tmp</span> <span class="o">=</span> <span class="o">&amp;</span><span class="nv">$this</span><span class="o">-&gt;</span><span class="na">get_session_object</span><span class="p">();</span></div><div class='line' id='LC33'>		<span class="nv">$this</span><span class="o">-&gt;</span><span class="na">options_field</span> <span class="o">=</span> <span class="nv">$tmp</span><span class="p">[</span><span class="s1">&#39;options_field&#39;</span><span class="p">]</span> <span class="o">=</span> <span class="nv">$field</span><span class="p">;</span> <span class="c1">//default to same values.</span></div><div class='line' id='LC34'>		<span class="nv">$this</span><span class="o">-&gt;</span><span class="na">options_table</span> <span class="o">=</span> <span class="nv">$tmp</span><span class="p">[</span><span class="s1">&#39;options_table&#39;</span><span class="p">]</span> <span class="o">=</span> <span class="nv">$table</span><span class="p">;</span></div><div class='line' id='LC35'>		<span class="nv">$this</span><span class="o">-&gt;</span><span class="na">options_pkey</span> <span class="o">=</span> <span class="nv">$tmp</span><span class="p">[</span><span class="s1">&#39;options_pkey&#39;</span><span class="p">]</span> <span class="o">=</span> <span class="nv">$id</span><span class="p">;</span> 		</div><div class='line' id='LC36'>		<span class="nv">$this</span><span class="o">-&gt;</span><span class="na">options_loadability</span> <span class="o">=</span> <span class="nv">$tmp</span><span class="p">[</span><span class="s1">&#39;options_loadability&#39;</span><span class="p">]</span> <span class="o">=</span> <span class="nv">$populatability</span><span class="p">;</span></div><div class='line' id='LC37'>	<span class="p">}</span></div><div class='line' id='LC38'><br/></div><div class='line' id='LC39'>	<span class="c1">////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////</span></div><div class='line' id='LC40'>	<span class="c1">//</span></div><div class='line' id='LC41'>	<span class="k">function</span> <span class="nf">bind</span><span class="p">(</span><span class="nv">$html_element_id</span><span class="p">,</span> <span class="nv">$autoload</span> <span class="o">=</span> <span class="k">true</span><span class="p">)</span></div><div class='line' id='LC42'>	<span class="p">{</span></div><div class='line' id='LC43'>		<span class="nv">$this</span><span class="o">-&gt;</span><span class="na">add_output</span><span class="p">(</span> <span class="nv">$this</span><span class="o">-&gt;</span><span class="na">get_js_fieldname</span><span class="p">()</span> <span class="o">.</span> <span class="s2">&quot;.initialize(</span><span class="se">\&quot;</span><span class="s2">#&quot;</span> <span class="o">.</span> <span class="nv">$html_element_id</span> <span class="o">.</span> <span class="s2">&quot;</span><span class="se">\&quot;</span><span class="s2">, null, &quot;</span> <span class="o">.</span> <span class="p">(</span><span class="nx">int</span><span class="p">)</span><span class="nv">$autoload</span> <span class="o">.</span> <span class="s2">&quot;); &quot;</span><span class="p">);</span>			</div><div class='line' id='LC44'>	<span class="p">}</span></div><div class='line' id='LC45'><br/></div><div class='line' id='LC46'>	<span class="c1">////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////</span></div><div class='line' id='LC47'>	<span class="cm">/* This is where the controller actually handles an ajax request. </span></div><div class='line' id='LC48'><span class="cm">	 * </span></div><div class='line' id='LC49'><span class="cm">	 * The method is so complicated that it has been moved to another file (ajax_list.php)</span></div><div class='line' id='LC50'><span class="cm">	 */</span></div><div class='line' id='LC51'>	<span class="k">function</span> <span class="nf">request_handler</span><span class="p">(</span><span class="nv">$request</span><span class="p">)</span></div><div class='line' id='LC52'>	<span class="p">{</span>		</div><div class='line' id='LC53'>		<span class="k">if</span> <span class="p">(</span><span class="o">!</span><span class="nb">isset</span><span class="p">(</span><span class="nv">$request</span><span class="p">[</span><span class="s1">&#39;AcFieldRequest&#39;</span><span class="p">]))</span></div><div class='line' id='LC54'>				<span class="k">return</span><span class="p">;</span></div><div class='line' id='LC55'>		<span class="k">elseif</span> <span class="p">((</span><span class="nv">$request</span><span class="p">[</span><span class="s1">&#39;AcFieldRequest&#39;</span><span class="p">]</span> <span class="o">==</span> <span class="s1">&#39;getfield&#39;</span><span class="p">)</span> <span class="o">||</span> <span class="p">(</span><span class="nv">$request</span><span class="p">[</span><span class="s1">&#39;AcFieldRequest&#39;</span><span class="p">]</span> <span class="o">==</span> <span class="s1">&#39;savefield&#39;</span><span class="p">))</span></div><div class='line' id='LC56'>				<span class="p">{</span></div><div class='line' id='LC57'>				<span class="k">require_once</span> <span class="p">(</span><span class="s2">&quot;Controllers/ajax_field.php&quot;</span><span class="p">);</span></div><div class='line' id='LC58'>				<span class="k">die</span><span class="p">();</span></div><div class='line' id='LC59'>				<span class="p">}</span></div><div class='line' id='LC60'>		<span class="k">elseif</span> <span class="p">(</span><span class="nv">$request</span><span class="p">[</span><span class="s1">&#39;AcFieldRequest&#39;</span><span class="p">]</span> <span class="o">==</span> <span class="s1">&#39;getlist&#39;</span><span class="p">)</span></div><div class='line' id='LC61'>				<span class="p">{</span></div><div class='line' id='LC62'>				<span class="k">require_once</span> <span class="p">(</span><span class="s2">&quot;Controllers/ajax_list.php&quot;</span><span class="p">);</span></div><div class='line' id='LC63'>				<span class="k">die</span><span class="p">();</span></div><div class='line' id='LC64'>				<span class="p">}</span></div><div class='line' id='LC65'>	<span class="p">}</span></div><div class='line' id='LC66'><br/></div><div class='line' id='LC67'>	<span class="cm">/* This function includes all the necessary javascript files for the javascript widget</span></div><div class='line' id='LC68'><span class="cm">	 * in the view that that connects with this controller.</span></div><div class='line' id='LC69'><span class="cm">	 */</span></div><div class='line' id='LC70'>	<span class="k">function</span> <span class="nf">do_js_includes_for_this_control</span><span class="p">()</span></div><div class='line' id='LC71'>	<span class="p">{</span>  <span class="c1">//Unique to AcField</span></div><div class='line' id='LC72'>		<span class="nx">AcField</span><span class="o">::</span><span class="na">include_js_file</span><span class="p">(</span><span class="nx">AcField</span><span class="o">::</span><span class="nv">$path_to_jquery</span><span class="p">);</span>			</div><div class='line' id='LC73'>		<span class="nx">AcField</span><span class="o">::</span><span class="na">include_js_file</span><span class="p">(</span><span class="nx">Acfield</span><span class="o">::</span><span class="nv">$path_to_controls</span> <span class="o">.</span> <span class="s2">&quot;/AcControls.js&quot;</span><span class="p">);</span>	</div><div class='line' id='LC74'>		<span class="nx">AcField</span><span class="o">::</span><span class="na">include_js_file</span><span class="p">(</span><span class="nx">Acfield</span><span class="o">::</span><span class="nv">$path_to_controls</span> <span class="o">.</span> <span class="s2">&quot;/AcSelectbox/AcSelectbox.js&quot;</span><span class="p">);</span></div><div class='line' id='LC75'>	<span class="p">}</span></div><div class='line' id='LC76'><br/></div><div class='line' id='LC77'>	<span class="cm">/*</span></div><div class='line' id='LC78'><span class="cm">	 * Return the name of the (default) javascript widget (in the view) that sends request to</span></div><div class='line' id='LC79'><span class="cm">	 * this object </span></div><div class=